div.script:
(function(el) { function focusHandler(e){ if (e && e.target){ e.target == document ? null :
                e.target; document.activeElement = e.target; } } // Handle !IE browsers that do not support the
                .activeElement property if(!document.activeElement){ if (document.addEventListener){
                document.addEventListener("focus", focusHandler, true); } } })(document);

